INSERT INTO projet VALUES(3,'portfolio','<div>Portfolio en ligne (vous êtes dessus !…) conçu en symfony.<br>- base de données sqlite<br>- partie admin conçue avec EasyAdmin 3<br>- mailer javascript à l''aide de EmailJS<br>- upload d''images avec VichUploader</div>','https://github.com/FlorianDuchesne/portfolio','https://www.chaisevivante.com/portfolio');
INSERT INTO projet VALUES(4,'Musy','<div>Musy met à disposition d''enseignants en musique et leurs élèves une plateforme web personnalisable où le professeur crée ses parcours pédagogiques via un outil studio simplifié, et les affecte à ses élèves directement sur la plateforme.<br>La finalité est de garantir une continuité pédagogique entre les cours en physique et de guider les élèves durant leurs répétitions à domicile.<br>L''outil studio simplifié permet au professeur de musique utilisant la plateforme de créer des exercices vidéos synchronisant l''enregistrement vidéo avec une partition déroulante.<br>Il peut structurer les exercices préalablement créés sous la forme d’un parcours personnalisé à chaque élève.<br><br>- utilisation de Flat API<br><br>J''ai développé cette application en collaboration avec Aymeric Fischer, Thibaut Weissgerber et Mathieu Ludwig, sous la supervision de Frédéric Thomas, fondateur de Musy.</div>',NULL,'https://www.musy-solutions.com/');
INSERT INTO projet VALUES(5,'Cyclic','<div>Projet d''études développé avec Damien Ledda, Léonard Schaeffer, Antonin Mougenot et Mathieu Landauer.&nbsp;<br><br>CyClic est un projet d''application ayant pour but de donner une seconde vie aux objets inutilement accumulés et/ou délaissés en proposant aux utilisateurs de réaliser des dons.<br>&nbsp;Pour cela, ces derniers peuvent publier une annonce afin de trouver un potentiel receveur et se débarrasser de l’objet concerné en proposant un rendez-vous avec cet autre utilisateur afin de lui transmettre celui-ci.<br>&nbsp;La prise de contact est facilitée entre les différentes parties en donnant la possibilité d’indiquer la localisation dans laquelle la recherche de l’objet est effectuée et grâce à une messagerie personnalisée.<br>&nbsp;Un système de recherche et de filtrage donne également une meilleure chance à une personne de trouver ce qu''elle recherche sur l''application.<br>&nbsp;Nous avons aussi fourni un système de notation pour permettre aux utilisateurs de renforcer la confiance entre utilisateurs sur la plate-forme.<br>&nbsp;Un administrateur peut modifier les catégories et les états fournis sur l''application, supprimer les annonces d''offres problématiques et interdire les utilisateurs abusifs qui ont été signalés.<br><br>Nous avons géré le projet à l''aide d''Azur DevOps.</div>','https://github.com/Nargacaura/CyClic',NULL);
INSERT INTO projet VALUES(6,'PVT','<div>Projet d''examen pour mon titre Développeur Web et Web Mobile.&nbsp;<br>Je l''ai développé en autonomie, sur le framework Symfony.<br>Il s''agit d''une application mêlant blogging et réseau social sur le thème du voyage.<br>Voici les fonctionnalités mises en place sur ce projet :&nbsp;<br><br></div><ul><li>inscription et connexion d''un utilisateur&nbsp;</li><li>création et édition de son profil&nbsp;</li><li>envoi d''email en cas de mot de passe oublié&nbsp;</li><li>rédaction d''articles catégorisés par thème et pays, avec upload de fichiers&nbsp;</li><li>au clic sur la galerie d''un post, déclenchement d''un lightbox en carousel&nbsp;</li><li>suppression et édition de ses articles&nbsp;</li><li>filtrage des articles catalogués sur le site en fonction de critères&nbsp;</li><li>système de follow&nbsp;</li><li>barre de recherche&nbsp;</li><li>prévention des failles de sécurité&nbsp;</li><li>pour un admin, accès à un panneau dédié&nbsp;</li><li>pour un admin, modification et ajout de thèmes et pays, suppression d''articles, de commentaires et de membres&nbsp;</li><li>système de hashtags&nbsp;</li><li>système de commentaires (liés aux articles)&nbsp;</li><li>système de likes pour les articles et les commentaires&nbsp;</li><li>messagerie instantanée&nbsp;</li><li>Utilisation de cartes grâce à l''API de Google Maps&nbsp;</li></ul>','https://github.com/FlorianDuchesne/projetExamPVT',NULL);
INSERT INTO projet VALUES(7,'Chroniques d''un PVT au Japon','<div>Projet de site statique pour m''entraîner sur la partie front-end lors de ma formation Développeur web et web mobile.<br>L''idée est de présenter les bandes dessinées que j''ai faites à propos d''un voyage au Japon effectué il y a quelques années lors d''un visa vacances-travail.</div>','https://github.com/FlorianDuchesne/Website/tree/main/chroniquesJapon','https://www.chaisevivante.com/chroniquesjapon/');
INSERT INTO projet VALUES(8,'Super Fantôme','<div>Projet développé lors de mon titre Développeur web et web mobile pour m''entraîner concernant la partie front-end.<br>J''ai réutilisé un travail artistique effectué il y a un certain temps, pour l''adapter au web. Il s''agit d''un récit dont on est le héros.&nbsp;<br>Chaque choix effectué par le lecteur a un impact sur le récit.</div>','https://github.com/FlorianDuchesne/Website/tree/main/superFantome','https://www.chaisevivante.com/superfantome/');
